import React, { useState, useEffect } from 'react';
import { Match, UserProfile } from '../types';
import { getRecommendations } from '../services/db';

interface MatchesListProps {
  matches: Match[];
}

const MatchesList: React.FC<MatchesListProps> = ({ matches }) => {
  // In a real app, we would fetch the user details for each match.
  // Here we just map match IDs back to our mock data for display purposes.
  // We grab "all users" to find the match partner.
  const [allUsers, setAllUsers] = useState<UserProfile[]>([]);

  useEffect(() => {
    const fetchUsers = async () => {
      const users = await getRecommendations();
      setAllUsers(users);
    };
    fetchUsers();
  }, []);

  return (
    <div className="h-full bg-white p-4 pb-20 overflow-y-auto no-scrollbar">
      <h1 className="text-2xl font-bold text-gray-900 mb-6">Ê∂àÊÅØ & ÂåπÈÖç</h1>

      {/* New Matches Row */}
      <div className="mb-8">
        <h2 className="text-sm font-semibold text-rose-500 uppercase tracking-wider mb-3">Êñ∞ÈÖçÂØπ</h2>
        <div className="flex gap-4 overflow-x-auto no-scrollbar pb-2">
          {(!matches || matches.length === 0) && <p className="text-gray-400 text-sm">ÊöÇÊó†Êñ∞ÈÖçÂØπÔºåÂø´ÂéªÊªëÂä®ÂêßÔºÅ</p>}
          {(matches || []).map(match => {
             // Hacky way to find "the other person"
             const partnerId = match.users[1]; // Assuming current user is always 0 in the mock creation logic
             const partner = allUsers.find(u => u.id === partnerId) || allUsers[0]; 

             return (
               <div key={match.id} className="flex flex-col items-center space-y-1 min-w-[4.5rem]">
                 <div className="relative">
                   <img src={partner?.avatar} alt={partner?.name} className="w-16 h-16 rounded-full object-cover border-2 border-rose-500 p-0.5" />
                   <div className="absolute bottom-0 right-0 w-3 h-3 bg-green-500 border-2 border-white rounded-full"></div>
                 </div>
                 <span className="text-xs font-medium text-gray-700 truncate w-full text-center">{partner?.name}</span>
               </div>
             )
          })}
        </div>
      </div>

      {/* Messages List */}
      <div>
        <h2 className="text-sm font-semibold text-gray-900 uppercase tracking-wider mb-3">Ê∂àÊÅØÂàóË°®</h2>
        <div className="space-y-4">
          {(matches || []).map(match => {
            const partnerId = match.users[1];
            const partner = allUsers.find(u => u.id === partnerId) || allUsers[0];

            return (
              <div key={match.id} className="flex items-center gap-4 p-3 hover:bg-gray-50 rounded-xl transition-colors cursor-pointer">
                 <img src={partner?.avatar} alt={partner?.name} className="w-14 h-14 rounded-full object-cover" />
                 <div className="flex-1 min-w-0">
                    <div className="flex justify-between items-baseline mb-0.5">
                      <h3 className="font-semibold text-gray-900 truncate">{partner?.name}</h3>
                      <span className="text-xs text-gray-400">ÂàöÂàö</span>
                    </div>
                    <p className="text-sm text-gray-500 truncate">{match.lastMessage || "ÂºÄÂßãËÅäÂ§©ÂêßÔºÅ"}</p>
                 </div>
              </div>
            )
          })}
           {(!matches || matches.length === 0) && (
             <div className="text-center py-10 opacity-50">
               <div className="w-16 h-16 bg-gray-100 rounded-full mx-auto mb-3 flex items-center justify-center">
                 <span className="text-2xl">üí§</span>
               </div>
               <p className="text-gray-500">‰ø°ÁÆ±Á©∫Á©∫ÁöÑ...</p>
             </div>
           )}
        </div>
      </div>
    </div>
  );
};

export default MatchesList;
